const API_URL = "https://www.hatchways.io/api/assessment/students1"

 const callAPI = () => {

  let data
  const promise = fetch(API_URL)
    .then(response => {
      response.json().then(
        res => {
          data=res
        }
      )
    })

  return {
    data: data,
    promise: promise
  }
}

export default callAPI
