import React, { Component } from 'react';
import './App.css';
import callAPI from './modules/FetchAPI'
import average from './modules/AverageGrades'

class App extends Component {
  constructor(props) {
    super(props)
    this.state = {
      students: null
    }
  }
  componentDidMount() {
    callAPI().then(data => {
      this.setState({
        students: data.students
      })
    })
  }
  render() {
    return (
      <div className="page">
        {this.state.students &&
          this.state.students.map(stu =>
            <div className="student" key={stu['id']}>
            <div>
              <img src={stu['pic']} alt=""/>
            </div>
              <div className="name">
                {stu['firstName']}&nbsp;{stu['lastName']}
              </div>
              <div className="info">
                <div>
                  Email:&nbsp;{stu['email']}
                </div>
                <div>
                  Company:&nbsp;{stu['company']}
                </div>
                <div>
                  Skill:&nbsp;{stu['skill']}
                </div>
                <div>
                  Average:&nbsp;{average(stu['grades'])}%
                </div>
              </div>
            </div>
          )
        }
      </div>
    );
  }
}

export default App;
