import React, { Component } from 'react';
import './App.css';
import callAPI from './modules/FetchAPI'
import average from './modules/AverageGrades'
import SearchInput from './components/searchInput'
import filter from './modules/filter'
import Expand from './components/Expand'

class App extends Component {
  constructor(props) {
    super(props)
    this.state = {
      students: null,
      input: "",
      displayedStudents: null,
      isShow: false
    }
  }
  componentDidMount() {
    callAPI().then(data => {
      this.setState({
        students: data.students
      })
    })
  }
  handleOnChange = (e) => {
    let result = filter(this.state.students, "name")(e.target.value)
    this.setState({
      displayedStudents: result,
      input: e.target.value
    })
  }
  handleExpandClick = (e) => {
    this.setState(prevState => ({ isShow: !prevState.isShow }))
  }
  render() {
    let students = this.state.displayedStudents || this.state.students
    return (
      <div className="page">
        <div className="box">
          <SearchInput change={this.handleOnChange} value={this.state.input} />
          <div className="students">
            {students &&
              students.map(stu =>
                <div className="student" key={stu['id']}>
                  {/* pic error handling */}
                  <div className="portrait">
                    <img src={stu['pic']} alt="portrait" />
                  </div>
                  <div className="info">
                    <div className="name">
                      {stu['firstName'].toUpperCase()}&nbsp;{stu['lastName'].toUpperCase()}
                    </div>
                    <div className="general_info">
                      <div>
                        Email:&nbsp;{stu['email']}
                      </div>
                      <div>
                        Company:&nbsp;{stu['company']}
                      </div>
                      <div>
                        Skill:&nbsp;{stu['skill']}
                      </div>
                      <div>
                        Average:&nbsp;{average(stu['grades'])}%
                      </div>
                      <Expand content={stu} isShow={true} />
                    </div>
                  </div>
                  <div className="expand_symbol" onClick={this.handleExpandClick}>
                  +
                    <div className="plus">

                    </div>
                    <div className="minus">

                    </div>
                  </div>
                </div>
              )
            }
          </div>
        </div>
      </div>
    );
  }
}

export default App;
