const addTag = (prevStudents, studentId, tagInput) => {
  let newStudents = []
  prevStudents.map((stu,index) => {
    if (stu["id"] === studentId) {
      let newStu = stu
      //add new tag
      if (!stu["tags"]) {
        newStu["tags"] = [tagInput]
      } else {
        stu["tags"].push(tagInput)
      }
      newStudents[index]=newStu
      return 
    }
    newStudents.push(stu)
  })
  return newStudents
}
export default addTag
