import React, { Component } from 'react';
import './App.css';
import callAPI from './modules/FetchAPI'
import SearchInput from './components/searchInput'
import filter from './modules/filter'
import Student from './components/Student'
import addTag from './modules/addTag'

class App extends Component {
  constructor(props) {
    super(props)
    this.state = {
      students: null,
      input: "",
      displayedStudents: null,
    }
  }
  componentDidMount() {
    callAPI().then(data => {
      this.setState({
        students: data.students
      })
    })
  }
  handleOnChange = (e) => {
    let result = filter(this.state.students, "name")(e.target.value)
    this.setState({
      displayedStudents: result,
      input: e.target.value
    })
  }
  submit = (id, event,input) => {
    if (event.key !== "Enter") return
    console.log(input);

    this.setState(prevState => ({
      students: addTag(prevState.students, id, input)
    }))
  }
  render() {
    let students = this.state.displayedStudents || this.state.students
    return (
      <div className="page">
        <div className="box">
          <SearchInput change={this.handleOnChange} value={this.state.input} />
          <div className="students">
            {students &&
              students.map(stu =>
                <Student student={stu} submit={this.submit} />
              )
            }
          </div>
        </div>
      </div>
    );
  }
}

export default App;
