const filter=(target,type)=>{
  const filterByName = (inputName)=>{
    let result=[]
    for (const person of target) {
      console.log(person);

      // if(person["firstName"].concat(person["lastName"]).indexOf(inputName)!==-1){
      //   result.push(person)
      // }
    }
    return result
  }
  const filterByTag = (inputTag)=>{
    let result=[]
    for (const person of target) {
      if(person["tag"].indexOf(inputTag)!==-1){
        result.push(person)
      }
    }
    return result
  }
  switch (type){
    case "name": return filterByName;
    case "tag": return filterByTag; 
  }
}
