import React from 'react'
import './expand.css'

export default function Expand({ content, isShow, handleTagChange, inputValue }) {
  return (
    <div className="expand_outbox">
      {isShow &&
        <div className="expand">
          {content["grades"].map((grade, index) =>
            <div >
              Test {index}:  {grade.concat("%")}
            </div>
          )}
          <div className="expand_tags">
          {content["tags"].map(tag=>
            <div>
              {tag}
            </div>
            )}
          </div>
          <div className="expand_input">
            <input type="text" onChange={handleTagChange} value={inputValue}/>
          </div>
        </div>
      }
    </div>
  )
}
